userDefinedLinkHintCss:
div > .vimiumHintMarker {
    padding: 2px 3px;
    background-color: #282828;
    border: 0;
    border-radius: 2px;
    box-shadow: inset 0 -2px 0 #1D2021;
    background-image: none;
}

div > .vimiumHintMarker span {
    font-size: 12px;
    font-weight: bold;
    text-shadow: none;
    color: #458588;
}

div > .vimiumHintMarker > .matchingCharacter {
    color: #83A598;
}

/* VOMNIBAR */

#vomnibar {
    background-color: #282828;
}

#vomnibar input {
    color: #EBDBB2;
    font: -moz-window;
    font-size: 20px;
    height: 34px;
    margin-bottom: 0;
    padding: 4px;
    background-color: #1D2021;
    border-radius: 3px;
    border: 1px solid #282828;
    box-shadow: #ADD8E6 0 0 1px;
    width: 100%;
    outline: none;
    box-sizing: border-box;
}

#vomnibar .vomnibarSearchArea {
    display: block;
    padding: 10px;
    background-color: #1D2021;
    border-radius: 4px 4px 0 0;
    border-bottom: 0 solid #ADD8E6;
}

#vomnibar ul {
    background-color: #282828;
    border-radius: 0 0 4px 4px;
    list-style: none;
    padding-top: 0;
    padding-bottom: 0;
    margin-block-start: 2px;
    margin-block-end: 0;
}

#vomnibar li {
    border-bottom: 1px solid #1D2021;
    line-height: 1.1em;
    padding: 7px 10px;
    font-size: 16px;
    color: #F8F8F2;
    position: relative;
    display: list-item;
    margin: auto;
}

#vomnibar li .vomnibarBottomHalf {
    font-size: 15px;
    margin-top: 3px;
    padding: 2px 0;
}

#vomnibar li .vomnibarSource {
    color: #689D6A;
    margin-right: 4px;
}

#vomnibar li .vomnibarRelevancy {
    position: absolute;
    right: 0;
    top: 0;
    padding: 5px;
    background-color: #282A36;
    color: #EBDBB2;
    font-family: monospace;
    width: 100px;
    overflow: hidden;
}

#vomnibar li .vomnibarUrl {
    color: #83A598;
}

#vomnibar li .vomnibarMatch {
    font-weight: bold;
    color: #EBDBB2;
}

#vomnibar li.vomnibarSelected {
    background-color: #181D24;
}

#vomnibar li em, #vomnibar li .vomnibarTitle {
    color: #EBDBB2;
    margin-left: 4px;
    font-weight: normal;
}

#vomnibar li em {
    font-style: italic;
}

#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
    color: #FABD2F;
}

#vomnibarInput::selection {
    background-color: #458588;
}
